import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { addTask, deleteTask } from '../store/store';

function TodoList() {
  const [task, setTask] = useState('');
  const tasks = useSelector((state) => state.tasks); // Sélectionner les tâches du store
  const dispatch = useDispatch(); // Pour dispatcher les actions

  const generateId = () => Math.random().toString(36).substr(2, 9);

  const handleAddTask = () => {
    if (task.trim()) {
      const newTask = { id: generateId(), name: task };
      dispatch(addTask(newTask)); // Ajouter une tâche via Redux
      setTask('');
    }
  };

  const handleDeleteTask = (id) => {
    dispatch(deleteTask(id)); // Supprimer une tâche via Redux
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96">
        <h1 className="text-2xl font-bold text-blue-600 mb-4 text-center">Liste des tâches</h1>

        {/* Task Input */}
        <div className="flex mb-4">
          <input
            type="text"
            value={task}
            onChange={(e) => setTask(e.target.value)}
            placeholder="Ajouter une tâche"
            className="flex-grow p-2 border border-gray-300 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            onClick={handleAddTask}
            className="bg-blue-600 text-white px-4 py-2 rounded-r-lg hover:bg-blue-500"
          >
            Ajouter
          </button>
        </div>

        {/* Task List */}
        <ul className="space-y-2">
          {tasks.map((task) => (
            <li key={task.id} className="flex justify-between items-center bg-gray-50 p-2 rounded-lg shadow-sm">
              <Link to={`/todos/${task.id}`} className="text-blue-600 hover:underline">
                {task.name}
              </Link>
              <button
                onClick={() => handleDeleteTask(task.id)}
                className="bg-red-500 text-white px-2 py-1 rounded-lg hover:bg-red-400"
              >
                Supprimer
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default TodoList;
